mapper.insert:

insert into cart (
cartNum, userId, gdsNum, cartStock 
)
values (
cart_seq.nextval, #{userId}, #{gdsNum}, #{cartStock}
)

mapper.select:
select * from orders
where userId = #{userId}
order by orderdate desc

mapper.update:
update goods
	set gdsStock = gdsStock - #{gdsStock}
where gdsNum = #{gdsNum}

update goods
	set gdsSell = gdsSell + #{gdsStock}
where gdsNum = #{gdsNum}

update reply
set
repCon = #{repCon}
where repNum = #{repNum}
